import React, {Component} from 'react';
import Navbar from './Navbar';
import Aux from '../hoc/Auxiliary';
import '../App.scss';

class SubmitTopic extends Component {
    render(){
        return(
            <Aux>
                <Navbar />
                <div>
                    <div class="full-width">
                        <div class="jumbotron contact-banner">
                            <p className="banner-description">Fake news is rampant in the subcontinent.<strong>Surkhi needs your help!</strong> </p>
                            <button className="btn btn-outline-warning learnHow">Learn How</button>
                            <p class="lead">Use this document as a way to quickly start any new project with a beautiful 
                            image header.<br /> All you get is this text, a navbar, a header, and a mostly barebones HTML document.</p>
                        </div>
                    </div>
                    <div className="contact-header">
                        <h1 className="contactUs-Title">Contact Us</h1>
                        <h2 className="contactUs-subTitle">
                            Your feedback and stories are always welcome. 
                        </h2>
                    </div>
                </div>
            </Aux>
        )
    }
}

export default SubmitTopic;
