import React from 'react';
import {BrowserRouter as Router, Route, Link} from 'react-router-dom';
import '../assets/stylesheets/App.scss';

const Post = (props) => {
    return(
        <div>
            <div className="container individual-post" key={props.key}>
                <Link to='/posts/:postId'>
                    <div className="row h-100">
                    <div className="col my-auto">
                        <div className="pic mx-auto"><img src={require(props.image)} width="80" height="80" alt=""/></div>
                    </div>
                    <div className="col-6 my-auto">
                        <div className="headline">{props.postTitle}</div>
                        <div className="description">{props.postSummary}</div>
                    </div>
                    <div className="col my-auto">
                        <div className="verdict mx-auto"><img src={require(props.verdict)} width="140" height="140" alt=""/></div>
                    </div>
                    </div>
                </Link>
            </div>
        </div>
    );
}

export default Post;
