import React, {Component} from 'react';
import '../App.scss'

class Post extends Component{
    render(){
        return(
            <div className="individual-post" key={this.props.key} onClick={this.props.click}>
                <div className="board">
                    <div className="truth"><img src={this.props.verdict} width="120" height="120" alt=""/></div>
                    <div className="body">
                        <div className="pic"><img src={this.props.post_image} width="80" height="80" alt=""/></div>
                        <div className="headline">{this.props.headline}</div>
                        <div className="description">{this.props.description}</div>
                    </div>
                </div>
            </div>
        );
    }
}

export default Post;
