import React, {Component} from 'react'
import Post from './Post';

import {Query} from "react-apollo";
import gql from "graphql-tag";

const GET_POSTS = gql`
{
    posts {
        key
        postTitle
        postSummary
        verdict
        category
        image
    }
}
`;

const Posts = () => {
        return(
        <React.Fragment>
            <Query query={GET_POSTS}>
                {({loading, error, data}) =>{
                    if (loading) return "Loading...";
                    if (error) return `Error! ${error.message}`;
                
                    let postArray = data['posts'].reverse()
                    return postArray.map((post) => {
                        return (
                        <Post 
                            headline = {post.postTitle}
                            description={post.postSummary} 
                            post_image = {'../server/server-images/' + post.image}
                            key = {post.key}
                            verdict = {'../assets/images/surkhi-meter-images/' + post.verdict} />
                        );
                    });
                }
            }
            </Query>
        </React.Fragment>
        );
} 

export default Posts;
